const styles = {
    filterButtonsContainer: {
      display: "flex",
      justifyContent: "space-between",
      alignItems: "center",
      margin: "20px 10px",
      flexDirection: "row",  
    },
    button: {
      backgroundColor: "white",
      color: "black",
      padding: "8px 15px", 
      margin: "0 5px",
      border: "2px solid black",
      borderRadius: "5px",
      cursor: "pointer",
      outline: "none",
    },
    largerButton: {
      width: "150px",
    },
    activeButton: {
      backgroundColor: "black",
      color: "white",
    },
  };
  
  export default styles;
